<template>
  <div id="app">

    <div id="page-container">
      <transition :name="transitionDirection === 1 ? 'slide-advance' : 'slide-retreat'">
        <component :is="currentPageComponent" @next="advancePage" :key="currentPageKey">
          <ActionBar @back="retreatPage" :currentPage="currentPage" />
        </component>
      </transition>
    </div>
  </div>
</template>

<script lang="ts">

import { Vue, Component, Prop } from 'vue-property-decorator';
import ActionBar from './components/action-bar.vue';

import WelcomePage from './pages/welcome-page.vue';
import WallpaperPage from './pages/wallpaper-page.vue';
import LockscreenPage from './pages/lockscreen-page.vue';
import HomescreenPage from './pages/homescreen-page.vue';
import EndPage from './pages/end-page.vue';

import './styles/main.scss';

@Component({
    name: 'app',
    components: {
      ActionBar,
    }
})
export default class App extends Vue {

  private internalCurrentPage: number = 0;
  private availablePages: any[] = [
    { component: WelcomePage, key: 'welcome' },
    { component: LockscreenPage, key: 'lockscreen' },
    { component: HomescreenPage, key: 'homescreen' },
    { component: WallpaperPage, key: 'wallpaper' },
    { component: EndPage, key: 'end' }
  ];

  private transitionDirection: number = 0;

  get currentPageComponent() {
    return this.availablePages[this.currentPage].component;
  }

  get currentPageKey() {
    return this.availablePages[this.currentPage].key;
  }

  get currentPage() {
    return this.internalCurrentPage;
  }

  advancePage() {
    this.transitionDirection = 1;

    this.$nextTick(() => {
      this.internalCurrentPage++;
      if (this.internalCurrentPage >= this.availablePages.length) {
        this.internalCurrentPage = this.availablePages.length - 1;

        // Send finished notification
        // @ts-ignore
        window.webkit.messageHandlers.xenhtml.postMessage('finish');
      }
    });
  }

  retreatPage() {
    this.transitionDirection = 0;

    this.$nextTick(() => {
      this.internalCurrentPage--;
      if (this.internalCurrentPage < 0) this.internalCurrentPage = 0;
    });
  }

}
</script>

